// src/App.js
import React, { useState } from "react";
import { inferImage } from "./api";
import InferResult from "./components/InferResult";

export default function App() {
  const [file, setFile] = useState(null);
  const [checkpoint, setCheckpoint] = useState(""); // optional: relative name under backend/checkpoints or absolute path
  const [imageSize, setImageSize] = useState(512);
  const [scoreThreshold, setScoreThreshold] = useState(0.3);
  const [topk, setTopk] = useState(200);
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState(null);
  const [err, setErr] = useState(null);

  const handleFile = (e) => {
    setFile(e.target.files[0]);
    setResult(null);
    setErr(null);
  };

  const handleRun = async () => {
    if (!file) {
      setErr("Please select an image file first");
      return;
    }
    setLoading(true);
    setErr(null);
    try {
      const res = await inferImage(file, { checkpoint: checkpoint || null, image_size: imageSize, score_threshold: scoreThreshold, topk });
      setResult(res);
    } catch (e) {
      console.error(e);
      setErr(String(e));
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="app">
      <div className="header">
        <h1>Agritech â€” Inference UI</h1>
      </div>

      <div style={{ marginTop: 10 }}>
        <div className="controls">
          <input type="file" accept="image/*" onChange={handleFile} />
          <label>
            Checkpoint (optional):
            <input style={{ marginLeft: 6, width: 260 }} placeholder="e.g. best_model.pth or /abs/path/to.pth" value={checkpoint} onChange={(e) => setCheckpoint(e.target.value)} />
          </label>
          <label>
            Image size:
            <input type="number" style={{ width: 80, marginLeft: 6 }} value={imageSize} onChange={(e) => setImageSize(Number(e.target.value))} />
          </label>
          <label>
            Score thresh:
            <input type="number" step="0.01" style={{ width: 80, marginLeft: 6 }} value={scoreThreshold} onChange={(e) => setScoreThreshold(Number(e.target.value))} />
          </label>
          <button onClick={handleRun} disabled={loading}>{loading ? "Running..." : "Run Inference"}</button>
        </div>

        {err && <div style={{ marginTop: 12, color: "crimson" }}>{err}</div>}

        <div style={{ marginTop: 12 }}>
          <div className="small">Tip: If your backend is not at http://localhost:8600, set REACT_APP_BACKEND_URL env when running dev.</div>
        </div>

        <InferResult result={result} />
      </div>
    </div>
  );
}
